import { ColorTranslator } from 'colortranslator'

export function convertColor(
    value,
    colorType,
) {
    try {
        return ColorTranslator[`to${colorType}`](value)
    } catch (error) {
        console.error(error)
        return ''
    }
}
export const ColorType = {
    HEX: 'HEX',
    HEXA: 'HEXA',
    RGB: 'RGB',
    RGBA: 'RGBA',
    HSL: 'HSL',
    HSLA: 'HSLA',
    CMYK: 'CMYK',
}