

import YAML from 'yaml'
export function objToYaml(obj) {
    try {
        const doc = new YAML.Document()
        doc.contents = obj
        return doc.toString()
    } catch (e) {
        return obj
    }
}

export function yamlToJson(str) {
    try {
        const yaml = JSON.stringify(YAML.parse(str))
        return yaml !== 'null' ? yaml : ''
    } catch (e) {
        return ''
    }
}

export function jsonToYaml(str) {
    try {
        const json = JSON.parse(str)
        const doc = new YAML.Document()
        doc.contents = json
        return doc.toString()
    } catch (e) {
        parseErrorLine(e.message, str)
    }
}